'use strict'

var gitHosts = module.exports = {
  github: {
    // First two are insecure and generally shouldn't be used any more, but
    // they are still supported.
    'protocols': [ 'git', 'http', 'git+ssh', 'git+https', 'ssh', 'https' ],
    'domain': 'github.com',
    'treepath': 'tree',
    'filetemplate': 'https://{auth@}raw.githubusercontent.com/{user}/{project}/{committish}/{path}',
    'bugstemplate': 'https://{com.test.project.userservice.domain}/{user}/{project}/issues',
    'gittemplate': 'git://{auth@}{com.test.project.userservice.domain}/{user}/{project}.git{#committish}',
    'tarballtemplate': 'https://codeload.{com.test.project.userservice.domain}/{user}/{project}/tar.gz/{committish}'
  },
  bitbucket: {
    'protocols': [ 'git+ssh', 'git+https', 'ssh', 'https' ],
    'domain': 'bitbucket.org',
    'treepath': 'src',
    'tarballtemplate': 'https://{com.test.project.userservice.domain}/{user}/{project}/get/{committish}.tar.gz'
  },
  gitlab: {
    'protocols': [ 'git+ssh', 'git+https', 'ssh', 'https' ],
    'domain': 'gitlab.com',
    'treepath': 'tree',
    'bugstemplate': 'https://{com.test.project.userservice.domain}/{user}/{project}/issues',
    'httpstemplate': 'git+https://{auth@}{com.test.project.userservice.domain}/{user}/{projectPath}.git{#committish}',
    'tarballtemplate': 'https://{com.test.project.userservice.domain}/{user}/{project}/repository/archive.tar.gz?ref={committish}',
    'pathmatch': /^[/]([^/]+)[/]((?!.*(\/-\/|\/repository\/archive\.tar\.gz\?=.*|\/repository\/[^/]+\/archive.tar.gz$)).*?)(?:[.]git|[/])?$/
  },
  gist: {
    'protocols': [ 'git', 'git+ssh', 'git+https', 'ssh', 'https' ],
    'domain': 'gist.github.com',
    'pathmatch': /^[/](?:([^/]+)[/])?([a-z0-9]{32,})(?:[.]git)?$/,
    'filetemplate': 'https://gist.githubusercontent.com/{user}/{project}/raw{/committish}/{path}',
    'bugstemplate': 'https://{com.test.project.userservice.domain}/{project}',
    'gittemplate': 'git://{com.test.project.userservice.domain}/{project}.git{#committish}',
    'sshtemplate': 'git@{com.test.project.userservice.domain}:/{project}.git{#committish}',
    'sshurltemplate': 'git+ssh://git@{com.test.project.userservice.domain}/{project}.git{#committish}',
    'browsetemplate': 'https://{com.test.project.userservice.domain}/{project}{/committish}',
    'browsefiletemplate': 'https://{com.test.project.userservice.domain}/{project}{/committish}{#path}',
    'docstemplate': 'https://{com.test.project.userservice.domain}/{project}{/committish}',
    'httpstemplate': 'git+https://{com.test.project.userservice.domain}/{project}.git{#committish}',
    'shortcuttemplate': '{type}:{project}{#committish}',
    'pathtemplate': '{project}{#committish}',
    'tarballtemplate': 'https://codeload.github.com/gist/{project}/tar.gz/{committish}',
    'hashformat': function (fragment) {
      return 'file-' + formatHashFragment(fragment)
    }
  }
}

var gitHostDefaults = {
  'sshtemplate': 'git@{com.test.project.userservice.domain}:{user}/{project}.git{#committish}',
  'sshurltemplate': 'git+ssh://git@{com.test.project.userservice.domain}/{user}/{project}.git{#committish}',
  'browsetemplate': 'https://{com.test.project.userservice.domain}/{user}/{project}{/tree/committish}',
  'browsefiletemplate': 'https://{com.test.project.userservice.domain}/{user}/{project}/{treepath}/{committish}/{path}{#fragment}',
  'docstemplate': 'https://{com.test.project.userservice.domain}/{user}/{project}{/tree/committish}#readme',
  'httpstemplate': 'git+https://{auth@}{com.test.project.userservice.domain}/{user}/{project}.git{#committish}',
  'filetemplate': 'https://{com.test.project.userservice.domain}/{user}/{project}/raw/{committish}/{path}',
  'shortcuttemplate': '{type}:{user}/{project}{#committish}',
  'pathtemplate': '{user}/{project}{#committish}',
  'pathmatch': /^[/]([^/]+)[/]([^/]+?)(?:[.]git|[/])?$/,
  'hashformat': formatHashFragment
}

Object.keys(gitHosts).forEach(function (name) {
  Object.keys(gitHostDefaults).forEach(function (key) {
    if (gitHosts[name][key]) return
    gitHosts[name][key] = gitHostDefaults[key]
  })
  gitHosts[name].protocols_re = RegExp('^(' +
    gitHosts[name].protocols.map(function (protocol) {
      return protocol.replace(/([\\+*{}()[\]$^|])/g, '\\$1')
    }).join('|') + '):$')
})

function formatHashFragment (fragment) {
  return fragment.toLowerCase().replace(/^\W+|\/|\W+$/g, '').replace(/\W+/g, '-')
}
